INSERT INTO `articles` VALUES ('32', '1', '日本出现两种新血型 科学家称或与福岛核事故有关', '<div class=\"js_img_share_area\" data-fid=\"f69e21f3446\" id=\"artical_real\" style=\"margin: 0px; padding: 25px 0px 0px; font-size: 14px; line-height: 24px; position: relative; color: rgb(43, 43, 43); font-family: simsun, arial, helvetica, clean, sans-serif;\">\r\n	<div class=\"js_selection_area\" data-fid=\"f1002af383e\" id=\"main_content\" style=\"margin: 0px; padding: 0px;\">\r\n		<p class=\"detailPic\" style=\"margin: 0px auto 10px; padding: 0px; text-indent: 28px; text-align: center; word-wrap: break-word; word-break: normal;\">\r\n			<img alt=\"日本出现两种新血型科学家称或与福岛核事故有关\" height=\"287\" src=\"http://p0.ifengimg.com/haina/2016_51/c22b0f030c302ee_w599_h344.jpg\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\" width=\"500\" /></p>\r\n		<p class=\"picIntro\" style=\"margin: 0px 0px 25px; padding: 0px; text-align: center; word-wrap: break-word; word-break: normal; font-family: 楷体_gb2312, 楷体;\">\r\n			日本出现新血型</p>\r\n		<p style=\"margin: 0px 0px 25px; padding: 0px; text-indent: 28px; text-align: justify; word-wrap: break-word; word-break: normal;\">\r\n			环球网报道记者余鹏飞据俄罗斯卫星网12月7日报道，日本科学家在本国发现两种新血型，新血型的出现可能与福岛核事故有关。</p>\r\n		<p style=\"margin: 0px 0px 25px; padding: 0px; text-indent: 28px; text-align: justify; word-wrap: break-word; word-break: normal;\">\r\n			报道称，专家目前找不到更加准确的原因。科学家认为，这种影响可能很快遍及全球。研究人员推测，未来人类可能会出现十余种新血型。</p>\r\n		<p style=\"margin: 0px 0px 25px; padding: 0px; text-indent: 28px; text-align: justify; word-wrap: break-word; word-break: normal;\">\r\n			另据俄罗斯&ldquo;z-news&rdquo;新闻网站7日报道，日本科学家发现的两种新血型与已知的I，II，III和IV完全不同。</p>\r\n		<p style=\"margin: 0px 0px 25px; padding: 0px; text-indent: 28px; text-align: justify; word-wrap: break-word; word-break: normal;\">\r\n			新的血液类型被称为Langereis血型和Junior血型，以区别于传统的分类。据科学家们说，这种现象与福岛核事故造成的放射性物质泄漏有关。</p>\r\n		<p style=\"margin: 0px 0px 25px; padding: 0px; text-indent: 28px; text-align: justify; word-wrap: break-word; word-break: normal;\">\r\n			报道引述医生的话称，预计在不久的将来会看到更多的变化。科学家预测未来至少会有15个新的血型出现。<span class=\"ifengLogo\"><a href=\"http://www.ifeng.com/\" style=\"text-decoration: none; color: rgb(0, 66, 118); font-weight: bold;\" target=\"_blank\"><img src=\"http://p2.ifengimg.com/a/2016/0810/204c433878d5cf9size1_w16_h16.png\" style=\"border: 0px; vertical-align: top; display: inline; margin: 0px; max-width: 100%; height: auto; padding-top: 3px;\" /></a></span></p>\r\n	</div>\r\n</div>\r\n<div class=\"box02\" style=\"margin: 0px 0px 25px; padding: 0px; height: 214px; color: rgb(43, 43, 43); font-family: simsun, arial, helvetica, clean, sans-serif; text-align: center;\">\r\n	<img src=\"http://p0.ifengimg.com/a/2016/0914/bd2723fc5f85843size30_w160_h214.jpg\" style=\"border: 0px; vertical-align: bottom; height: 214px;\" /></div>\r\n', '2016-12-11 19:37', '9', '0', '3', '3', '1', '0');
INSERT INTO `articles` VALUES ('33', '1', 'Java Web 作业发布', '<p align=\"left\" class=\"MsoNormal\">\r\n	<b><span lang=\"EN-US\">1.</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">划分角色和权限：超级管理员（所有用户的管理：增加、删除、查询用户）；版主（可以删除本版的帖子）；普通用户（可以增删改自己发布的帖子，可以查看并评论所有帖子）；游客（可以查看所有帖子，不能评论）；</span><span lang=\"EN-US\" style=\"color:red\"><o:p></o:p></span></b></p>\r\n<p align=\"left\" class=\"MsoNormal\">\r\n	<span lang=\"EN-US\">&nbsp;</span></p>\r\n<p align=\"left\" class=\"MsoNormal\">\r\n	<b><span lang=\"EN-US\">2.</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">经验值：根据登录次数、</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\r\nmso-hansi-font-family:&quot;Times New Roman&quot;\">登录时长、<span style=\"color:red\">发布帖子数量、评论帖子数量，给予经验值，并且根据经验值给出对应的职位或者称呼；（黄金，白银</span></span><span lang=\"EN-US\" style=\"color:red\">&hellip;</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">）</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">（<span style=\"color:red\">评论加</span></span><span lang=\"EN-US\" style=\"color:red\">1</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">分</span><span lang=\"EN-US\" style=\"color:red\">,</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:\r\n&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;;color:red\">发表加</span><span lang=\"EN-US\" style=\"color:red\">2</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">分</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">，<span style=\"color:red\">做版主加</span></span><span lang=\"EN-US\" style=\"color:red\">50</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">分</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">，<span style=\"color:red\">删贴扣</span></span><span lang=\"EN-US\" style=\"color:red\">2</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">分</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;\">，<span style=\"color:red\">删评论扣</span></span><span lang=\"EN-US\" style=\"color:red\">1</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:\r\n&quot;Times New Roman&quot;;color:red\">分，注册预置</span><span lang=\"EN-US\" style=\"color:red\">10</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:\r\n&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;;color:red\">分</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:\r\n&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;\">，<span style=\"color:red\">登录一次加</span></span><span lang=\"EN-US\" style=\"color:red\">1</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:\r\n&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;;color:red\">分</span><span lang=\"EN-US\" style=\"color:red\">,</span></b><b><span style=\"font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hans