-- ----------------------------
-- Procedure structure for demo_out
-- ----------------------------
DROP PROCEDURE IF EXISTS `demo_out`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `demo_out`(out s int)
begin
select count(*) into s from articles;
end
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for p_test
